/*jshint esversion: 6 */

var _actionHandler;
try {
    _actionHandler = require("../../../src/custom_nodes/action-handler/_action-handler");
} catch (e) {
    _actionHandler = () => {};
}
module.exports = function (RED) {
    function actionHandlerNode(n) {
        RED.nodes.createNode(this, n);
        this.nodetype = n.nodetype;
        this.params = n.params;
        this.appId = n.appId;
        var node = this;
        node.on('input', function (msg) {
            _actionHandler(RED, msg, node);
        });
    }
    RED.nodes.registerType("action-handler", actionHandlerNode);
};