/*jshint esversion: 6 */

var dataInjector;
try {
    dataInjector = require('../../../src/custom_nodes/dataInjector/_dataInjector');
} catch (e) {
    dataInjector = function () {};
}

module.exports = function (RED) {
    function injectHandler(config) {
        RED.nodes.createNode(this, config);
        let node = this;
        try {
            let injector = new dataInjector(RED.settings.webosService, config);
            injector.subscribe(function (key, data) {
                let payload = {
                    "data": data.data
                };
                node.send({
                    "payload": payload
                });
            });
        } catch (e) {
            console.log("data-inject node init");
        }
    }
    RED.nodes.registerType("data-inject", injectHandler);
};