var dataPublisher;
try {
    dataPublisher = require('../../../src/custom_nodes/dataPublisher/_dataPublisher');
} catch (e) {
    dataPublisher = function () {};
}

module.exports = function (RED) {
    function publishHandler(config) {
        RED.nodes.createNode(this, config);
        let node = this;
        node.on('input', function (msg) {
            try {
                let publisher = new dataPublisher(RED.settings.webosService, config);
                publisher.publish(msg);
            } catch (e) {
                console.log("Notification node init");
            }
        });
    }
    RED.nodes.registerType("data-publish", publishHandler);
}