var _generateFact = {};

try {
    _generateFact = require('../../../src/custom_nodes/generate-fact/_generate-fact');
} catch (e) {
    _generateFact.voice = () => {};
};

module.exports = function (RED) {
    function generateFact(config) {
        RED.nodes.createNode(this, config);
        var node = this;
        node.on('input', function (msg) {
            var enginePayload = msg.payload,
                msgCopy = JSON.parse(JSON.stringify(msg));
            try {
                if (config.nodetype === 'Voice' && enginePayload.response) {
                    msg.payload = _generateFact.voice(enginePayload);
                }
            } catch (e) {
                console.log("generateFact node init");
            }
            if (config.rawdata) { // if "Raw data" is checked, send the engine rawdata to output node 2
                node.send([msg, msgCopy]);
            } else {
                node.send(msg);
            }
        });
    }
    RED.nodes.registerType("generate-fact", generateFact);
}