var AIEngine;
try {
    notificationHandler = require('../../../src/custom_nodes/notifications/_notificationshandler');
} catch (e) {
    AIEngine = class aiEngine {};
}
module.exports = function (RED) {
    function notificationsNode(config) {
        RED.nodes.createNode(this, config);
        let node = this;
        node.on('input', function (msg) {
            try {
                if (!config.message && msg.payload.voice.entity.ttsResponse) {
                    config.message = msg.payload.voice.entity.ttsResponse;
                }
                var _notification = new notificationHandler(RED.settings.webosService, config);
                _notification.toast()
            } catch (e) {
                console.log("Notification node init");
            }
            node.send(msg);
        });
    }
    RED.nodes.registerType("notifications", notificationsNode);
}