/*jshint esversion: 6 */

var AIEngine;
try {
    AIEngine = require('../../../src/custom_nodes/aiengine/_aiEngine');
} catch (e) {
    AIEngine = class aiEngine {};
}
module.exports = function (RED) {
    function voiceNode(config) {
        RED.nodes.createNode(this, config);
        let node = this;
        try {
            var _engine = new AIEngine(RED.settings.webosService, config, "voice");
            _engine.startAIEngine()
                .then(() => {
                    _engine.subscribeEngine(handleAIResponse);
                })
                .catch((error) => {
                    console.log(error);
                });
        } catch (e) {
            console.log("voice node init");
        }
        function handleAIResponse(rawdata) {
            if (rawdata.payload.response) {
                node.send({
                    "payload": rawdata.payload
                });
            }
        }
    }
    RED.nodes.registerType("voice", voiceNode);
};